sp_widget.client_script:
api.controller = function($scope, $element, $timeout) {
  var c = this;
  var scroller;

  c.steps = [
    {
      id: 1,
      title: "Submit the record",
      detail: "Complete the metadata form and click Submit to generate your new idea record."
    },
    {
      id: 2,
      title: "Open the idea table",
      detail: "In the Navigator, open x_snc_hack4good_0_hack4good_idea.list to view all idea records."
    },
    {
      id: 3,
      title: "Select your entry",
      detail: "Use the filter or sorting to locate the record you just submitted, then open it."
    },
    {
      id: 4,
      title: "Create application file",
      detail: "From the Actions menu, choose Create application file to register the record with the app."
    },
    {
      id: 5,
      title: "Confirm the dialog",
      detail: "When the Create Application File from Record dialog appears, click OK to generate the file."
    },
    {
      id: 6,
      title: "Commit in Studio",
      detail: "Launch Studio for the scope, review the pending change, and commit it to your local repo."
    },
    {
      id: 7,
      title: "Sync to GitHub",
      detail: "Push the committed update to your fork so the new application file is available online."
    },
    {
      id: 8,
      title: "Open a pull request",
      detail: "In GitHub, compare your fork with the upstream repo and submit a Pull Request for review."
    }
  ];

  c.canScrollLeft = false;
  c.canScrollRight = false;

  c.scroll = function(direction) {
    if (!scroller) {
      return;
    }
    var distance = Math.max(scroller.clientWidth * 0.8, 240);
    scroller.scrollLeft += distance * direction;
    $timeout(updateScrollState, 250);
  };

  function updateScrollState() {
    if (!scroller) {
      return;
    }
    var tolerance = 5;
    var maxScrollLeft = scroller.scrollWidth - scroller.clientWidth;
    c.canScrollLeft = scroller.scrollLeft > tolerance;
    c.canScrollRight = scroller.scrollLeft < (maxScrollLeft - tolerance);
    $scope.$applyAsync();
  }

  $timeout(function() {
    scroller = $element[0].querySelector('.h4g-tutorial-track');
    if (!scroller) {
      return;
    }
    updateScrollState();
    scroller.addEventListener('scroll', updateScrollState);
  }, 0);

  $scope.$on('$destroy', function() {
    if (scroller) {
      scroller.removeEventListener('scroll', updateScrollState);
    }
  });
};

