sp_widget.script:
(function () {
  // --- constants ---
  var CONTRIBUTING_URL = 'https://example.com/your/CONTRIBUTING.md'; // fill me

  // --- helpers ---
  function normalizeRepo(url) {
    if (!url) return '';
    url = ('' + url).trim();
    if (url.endsWith('.git')) url = url.slice(0, -4);
    return url.replace(/\/+$/, '');
  }

  function pluginActive(pluginId) {
    try {
      var pm = new GlidePluginManager();
      if (pm && pm.isActive(pluginId)) return true;
    } catch (e) { /* scoped envs may block direct call */ }
    // Fallback: query sys_plugins
    var sp = new GlideRecord('sys_plugins');
    sp.addQuery('id', pluginId);
    sp.addQuery('active', true);
    sp.setLimit(1);
    sp.query();
    return sp.hasNext();
  }

  // === Part 1: Proposal label (display field project_name) ===
  data.proposal = '';
  var ideaId = $sp.getParameter('idea'); // ?idea=<sys_id>
  if (ideaId) {
    var pr = new GlideRecord('x_snc_hack4good_0_hack4good_proposal');
    if (pr.get(ideaId)) data.proposal = pr.getDisplayValue(); // project_name
  }

  // === Part 2: Repo info from sys_repo_config (current app) ===
  data.repo = '';
  data.branch = '';
  data.github_user = '';
  data.contributing_url = CONTRIBUTING_URL;
  data.fork_branch_url = '';
  data.pr_url = '';

  var appId = gs.getCurrentApplicationId(); // current scoped app sys_id
  if (appId) {
    var rc = new GlideRecord('sys_repo_config');
    rc.addQuery('sys_app', appId);
    rc.orderByDesc('sys_updated_on');
    rc.setLimit(1);
    rc.query();
    if (rc.next()) {
      var repoUrl = normalizeRepo(rc.getValue('url'));
      var branch  = rc.getValue('current_branch') || 'main';

      // credential -> GitHub username (best-effort)
      var ghUser = '';
      try {
        var cred = rc.getElement('credential') && rc.getElement('credential').getRefRecord();
        if (cred && cred.isValidRecord()) {
          if (cred.isValidField('user_name')) ghUser = cred.getValue('user_name');
          else if (cred.isValidField('username')) ghUser = cred.getValue('username');
          else ghUser = cred.getDisplayValue('user_name') || cred.getDisplayValue();
        }
      } catch (e) {}

      data.repo = repoUrl;
      data.branch = branch;
      data.github_user = ghUser;

      if (repoUrl) {
        data.fork_branch_url = repoUrl + '/tree/' + branch;
        data.pr_url = repoUrl + '/compare/' + branch + '?expand=1';
      }
    }
  }

  // === Part 3: Build Studio deep-link (new Studio on Zurich, else classic Studio) ===
  data.studio_url = '';
  data.studio_label = 'Open in ServiceNow Studio';
  if (appId) {
    var buildName = (gs.getProperty('glide.buildname') || '').toLowerCase(); // e.g., 'zurich'
    var isZurich  = buildName.indexOf('zurich') === 0;
    var hasNewStudio = pluginActive('sn_sns'); // new ServiceNow Studio plugin

    if (isZurich && hasNewStudio) {
      // New Studio (supported from Zurich)
      data.studio_url = '/now/servicenow-studio/builder?table=sys_app&sysId=' + appId;
      data.studio_label = 'Open in ServiceNow Studio';
    } else {
      // Classic Studio fallback
      data.studio_url =
        '/$studio.do?sysparm_transaction_scope=' + appId +
        '&sysparm_nostack=true&sysparm_use_polaris=true';
      data.studio_label = 'Open in Studio (classic)';
    }
  }

  // Optional: suggested commit message
  (function buildCommitMessage(){
    var who = data.github_user ? ('@' + data.github_user) : 'contributor';
    var what = data.proposal || 'proposal';
    data.commit_message = 'feat(idea): ' + what + ' â€” initial commit by ' + who;
  })();
})();
