sp_widget.script:
(function () {
  // Config (tweak if you ever rename things)
  var PRODUCER_NAME = 'Hack4Good Idea Submission';
  var SUBMITTED_PAGE_ID = 'h4g_submitted_ideas';
  var HOWTO_URL = 'https://example.com/placeholder-howto'; // TODO: replace

  // Get current portal path (e.g., "/h4g")
  var portal = $sp.getPortalRecord();
  var suffix = portal ? (portal.getValue('url_suffix') || 'sp') : 'sp';
  var base   = '/' + suffix;

  // Resolve Record Producer by name (sys_id differs per instance)
  var producerId = '';
  var rp = new GlideRecord('sc_cat_item_producer');
  rp.addQuery('name', PRODUCER_NAME);
  rp.setLimit(1);
  rp.query();
  if (rp.next()) producerId = rp.getUniqueValue();

  // Build tiles
  var tiles = [];

  // Submit tile (disable if producer not found)
  tiles.push({
    key: 'submit',
    title: 'Submit an idea',
    desc: 'Propose a new project for Hack4Good.',
    href: producerId ? (base + '?id=idea_submission&sys_id=' + producerId) : '',
    icon: 'fa-paper-plane',
    disabled: !producerId
  });

  // Submitted ideas
  tiles.push({
    key: 'ideas',
    title: 'Submitted ideas',
    desc: 'Browse proposals submitted by the community.',
    href: base + '?id=' + SUBMITTED_PAGE_ID,
    icon: 'fa-th-large',
    disabled: false
  });

  // How-to
  tiles.push({
    key: 'howto',
    title: 'How to',
    desc: 'Learn how to contribute and open a Pull Request.',
    href: HOWTO_URL,
    icon: 'fa-graduation-cap',
    disabled: false,
    external: true
  });

  data.tiles = tiles;

  // Optional: surface producer status for a gentle note
  data.producer_found = !!producerId;
  data.producer_name  = PRODUCER_NAME;
})();
